import React, { useState } from "react";

const Digital = () => {
  const [name, setName] = useState("Hello !");
    
 let clr = "red"
 const[bg , setBg] = useState(clr)
  const clickhandler =()=>{
    setName("Hellow Sanjay Gupta !")
    let newclr = "teal"

    setBg(newclr)
  }

  const backnormal =()=>{
    setBg("yellow")
    setName("")
  }
  return (
    <div style={{backgroundColor:bg , padding:"40px" , textAlign:"center" ,}}>
      <button style={{padding:"20px 60px" ,fontSize:"30px"}} onDoubleClick={backnormal} onClick={clickhandler}>{name}</button>
    </div>
  );
};

export default Digital;
