import React, { useReducer, useState } from "react";
import "./Reduce.css"



const initialState = 5 

const reducer = (state , action)=>{
   
    if(action.type ==="Increment"){
        
        return state +1
    }

    if(action.type ==="decrement"){

        return  state-1 
    }

    if(action.type==="sanjay gupta is good man !"){

        return state
    }
    console.log(state,action)
    return state
}
const Reduce = ()=>{

    // const [num , setNum] = useState(0)

    const [state , dispatch] = useReducer(reducer, initialState) 
    return(

        <div className="data">
            <h1>{state}</h1>
            <button onClick={()=>dispatch({type:"Increment"})}>like</button><br /><br />
            <button onClick={()=>dispatch({type:"decrement"})}>unlike</button><br /><br />
            <button onClick={()=>dispatch({type:"`sanjay gupta is good man !`"})}>comment</button>
        </div>
    )
}

export  default Reduce
