import React, { useReducer, useState } from "react";
import "./Reduce.css";
const initialState = 0 
const name = "good app"
const reducer =(state, action )=>{
    if(action.type==="Like"){

        return state + 1;
    }

    if(action.type==="Unlike"){
        return state-1
    }
    if(action.type==="data"){
        return name
    }
    console.log(state, action)
    return state
}

const Reduce = () => {
  

