import React, { useState } from "react";
import "./Reduce.css"
const Reduce = ()=>{

    const [num , setNum] = useState(0)
    return(

        <div className="data">
            <h1>{num}</h1>
            <button onClick={()=>setNum(num+1)}>like</button><br /><br />
            <button onClick={()=>setNum(num-1)}>unlike</button><br /><br />
            <button onClick={()=>setNum("`sanjay gupta is good man`")}>comment</button>
        </div>
    )
}

export  default Reduce
